public class Weltraumschrott extends Circle {
   int geschwindigkeit;
   public Weltraumschrott(double x, double y, double radius, int color, int v_x) {
      super(x, y, radius);
      setFillColor(color);
      geschwindigkeit = -v_x;
   }
   public void act() {
      if(collidesWithAnyShape()) {
         destroy();
      }
      else {
         move(geschwindigkeit, 0);
      }
      if(centerX < 0) {
         destroy();
      }
   }
}

class Raumschiff extends Sprite {
   // Attribute der Klasse Raumschiff
   String name; // Raumschiffname
   double vx; // Geschwindigkeit in x-Richtung (rechts/links)
   double vy; // Geschwindigkeit in y-Richtung (auf/ab)
   double a; // Beschleunigung: Änderung von vx oder vy: z.B. vx = vx + a bei Tastendruck
   // Konstruktor
   Raumschiff(String name, double x, double y) {
      // Konstruktor der Klasse Sprite
      super(x, y, SpriteLibrary.Space_Shooter_1, 2);
      this.name = name;
      a = 0.3;
      vx = 0.0;
      vy = 0.0;
   }
   // act()-Methode
   public void act() {
      // Reaktion auf Cursertasten
      if(isKeyDown(Key.ArrowLeft)) {
         vx = vx - a;
      }
      if(isKeyDown(Key.ArrowRight)) {
         vx = vx + a;
      }
      if(isKeyDown(Key.ArrowUp)) {
         vy = vy - a;
      }
      if(isKeyDown(Key.ArrowDown)) {
         vy = vy + a;
      }
      // Etwas Gravitation in Erdnähe
      vy = vy + 0.03;

      // Bewegung um dx, dy
      move(vx, vy);

      // Raumschiff im Bildbereich halten
      if(getCenterY() < 32) {
         moveTo(getCenterX(), 32); // x-Koordinate bleibt, y wird 32
         vy = 0;
      }
      if(getCenterY() > 568) {
         moveTo(getCenterX(), 568); // x-Koordinate bleibt, y wird 0
         vy = 0;
      }

      if(getCenterX() < 32) {
         moveTo(32, getCenterY());
         vx = 0;
      }

      if(getCenterX() > 800) {
         moveTo(800, getCenterY());
         vx = 0;
      }
      if(collidesWithAnyShape()) {
         Points.points++;
      }
   }
}

Raumschiff hunter = new Raumschiff("Enterprise", 400, 300);
//Weltraumschrott
int v_Schrott = Input.readInt("Bitte Geschwindigkeit für Weltraumschrott eingeben (1-10)", 3);
while (true) {
   if(Zufall.gibInteger(0, 5000) == 50) {
      int x = Zufall.gibInteger(850, 900);
      int y = Zufall.gibInteger(10, 590);
      int radius = Zufall.gibInteger(10, 20);
      int farbe = Zufall.gibInteger(0, 16581375);
      new Weltraumschrott(x, y, radius, farbe, v_Schrott);
   }
   if(Points.points == 10) {
      break;
   }
}
System.out.println("Congrats, you have won!");
